import requests
import streamlit as st

def conselho():
    endpoint = "https://api.adviceslip.com/advice"
    response = requests.get(endpoint)
    
    conselho = response.json()['slip']['advice']
    return conselho

st.image('data:image/jpeg;base64,')
st.title('Here some advice for you!!')

adv = conselho()
st.text(adv)

st.text('Press F5 for another piece of advice.')
